mapper.update:
UPDATE goods
SET goods_state = #{state}
WHERE goods_id = #{gid}

mapper.select:
SELECT * FROM goods g INNER JOIN (SELECT goods_id,SUM(detail_num) AS num FROM order_detail GROUP BY goods_id ORDER BY num DESC ) o ON g.`goods_id` = o.goods_id INNER JOIN brand b ON b.`brand_id` = g.`brand_id` WHERE b.`category_id` IN (SELECT category_id FROM category WHERE category_parent_id IN ( SELECT category_id FROM category WHERE
category_name =#{item}
)) and g.goods_state = 1 ORDER BY num DESC LIMIT 10

SELECT g.* FROM goods g INNER JOIN brand b ON b.`brand_id` = g.`brand_id` WHERE b.`category_id` IN (SELECT category_id FROM category WHERE category_parent_id IN (SELECT category_id FROM category WHERE
category_name =#{item}
)) and g.goods_state = 1 ORDER BY g.`goods_create_time` DESC LIMIT 10

SELECT g.* FROM goods g INNER JOIN brand b ON b.`brand_id` = g.`brand_id` WHERE b.`category_id` IN (SELECT category_id FROM category WHERE category_parent_id IN (SELECT category_id FROM category WHERE
category_name =#{item}
)) and g.goods_state = 1 ORDER BY g.`goods_price` ASC LIMIT 10

SELECT g.*
FROM goods g
INNER JOIN brand b ON b.`brand_id` = g.`brand_id`
WHERE b.`category_id` IN
(SELECT category_id FROM category
WHERE

    category_id = #{item}

)

    and g.`goods_price` >= #{low}


    and g.`goods_price` <= #{high}

and g.goods_state = 1

SELECT * FROM goods g INNER JOIN (SELECT goods_id,SUM(detail_num) AS num FROM order_detail GROUP BY goods_id ORDER BY num DESC ) o ON g.`goods_id` = o.goods_id INNER JOIN brand b ON b.`brand_id` = g.`brand_id` WHERE b.`category_id` IN ( SELECT category_id FROM category WHERE
category_id = #{item}
) and g.goods_state = 1 ORDER BY num DESC

SELECT g.* FROM goods g INNER JOIN ( SELECT goods_id,SUM(detail_num) AS num,order_id AS oid FROM order_detail GROUP BY goods_id ) o ON g.`goods_id` = o.goods_id INNER JOIN orders r ON r.order_id = o.oid INNER JOIN brand b ON b.`brand_id` = g.`brand_id` WHERE b.`category_id` IN ( SELECT category_id FROM category WHERE
category_id = #{item}
) and g.goods_state = 1 AND TO_DAYS(NOW())- TO_DAYS(r.order_time)<=7 ORDER BY num DESC LIMIT 6

SELECT g.goods_id id,goods_title title,goods_sub_title sub,goods_main_img img,goods_create_time times,goods_price price,num FROM goods g INNER JOIN (SELECT goods_id,SUM(detail_num) AS num FROM order_detail GROUP BY goods_id ORDER BY num DESC ) o ON g.`goods_id` = o.goods_id INNER JOIN brand b ON b.`brand_id` = g.`brand_id` WHERE b.`category_id` IN ( SELECT category_id FROM category WHERE
category_id = #{item}
) ORDER BY num DESC
